// SetMemberUsers removes all previously related items of the
// team replacing them completely with the passed
// in related items, optionally inserting them as new records.
// Sets o.R.Teams's MemberUsers accordingly.
// Replaces o.R.MemberUsers with related.
// Sets related.R.Teams's MemberUsers accordingly.
func (o *Team) SetMemberUsers(ctx context.Context, exec boil.ContextExecutor, insert bool, related ...*User) error {
	query := "delete from \"team_members\" where \"team_id\" = $1"
	values := []interface{}{o.ID}
	if boil.IsDebug(ctx) {
		writer := boil.DebugWriterFrom(ctx)
		fmt.Fprintln(writer, query)
		fmt.Fprintln(writer, values)
	}
	_, err := exec.ExecContext(ctx, query, values...)
	if err != nil {
		return errors.Wrap(err, "failed to remove relationships before set")
	}

	removeMemberUsersFromTeamsSlice(o, related)
	if o.R != nil {
		o.R.MemberUsers = nil
	}

	return o.AddMemberUsers(ctx, exec, insert, related...)
}

// RemoveMemberUsers relationships from objects passed in.
// Removes related items from R.MemberUsers (uses pointer comparison, removal does not keep order)
// Sets related.R.Teams.
func (o *Team) RemoveMemberUsers(ctx context.Context, exec boil.ContextExecutor, related ...*User) error {
	if len(related) == 0 {
		return nil
	}

	var err error
	query := fmt.Sprintf(
		"delete from \"team_members\" where \"team_id\" = $1 and \"member_id\" in (%s)",
		strmangle.Placeholders(dialect.UseIndexPlaceholders, len(related), 2, 1),
	)
	values := []interface{}{o.ID}
	for _, rel := range related {
		values = append(values, rel.ID)
	}

	if boil.IsDebug(ctx) {
		writer := boil.DebugWriterFrom(ctx)
		fmt.Fprintln(writer, query)
		fmt.Fprintln(writer, values)
	}
	_, err = exec.ExecContext(ctx, query, values...)
	if err != nil {
		return errors.Wrap(err, "failed to remove relationships before set")
	}
	removeMemberUsersFromTeamsSlice(o, related)
	if o.R == nil {
		return nil
	}

	for _, rel := range related {
		for i, ri := range o.R.MemberUsers {
			if rel != ri {
				continue
			}

			ln := len(o.R.MemberUsers)
			if ln > 1 && i < ln-1 {
				o.R.MemberUsers[i] = o.R.MemberUsers[ln-1]
			}
			o.R.MemberUsers = o.R.MemberUsers[:ln-1]
			break
		}
	}

	return nil
}

func removeMemberUsersFromTeamsSlice(o *Team, related []*User) {
	for _, rel := range related {
		if rel.R == nil {
			continue
		}
		for i, ri := range rel.R.Teams {
			if o.ID != ri.ID {
				continue
			}

			ln := len(rel.R.Teams)
			if ln > 1 && i < ln-1 {
				rel.R.Teams[i] = rel.R.Teams[ln-1]
			}
			rel.R.Teams = rel.R.Teams[:ln-1]
			break
		}
	}
}

